<?php
// 检测是否为移动设备尺寸
function isMobile() {
    $userAgent = $_SERVER['HTTP_USER_AGENT'] ?? '';
    // 检测微信客户端
    $isWeChat = (strpos($userAgent, 'MicroMessenger') !== false);
    // 检测移动设备
    $isMobile = (preg_match('/(android|webos|iphone|ipad|ipod|blackberry|windows phone|mobile)/i', $userAgent) > 0);
    return $isWeChat || $isMobile;
}
